mapper.insert:
insert into infrMember (
ifmmId
,ifmmName
,ifmmDelNy
,regDateTime
)
values (
#{ifmmId}
,#{ifmmName}
,0
,#{regDateTime}
)

SELECT last_insert_id()

mapper.select:
SELECT a.ifmmSeq ,a.ifmmName ,a.ifmmId ,a.ifmmDelNy ,a.regDateTime
FROM infrMember a
WHERE 1=1 AND a.ifmmSeq = #{ifmmSeq}

mapper.update:
UPDATE
infrMember  
SET
ifmmName = #{ifmmName} 
,ifmmId = #{ifmmId}
WHERE 
AND ifmmSeq = #{ifmmSeq}

UPDATE
	infrMember  
SET
	ifmmDelNy = 1;
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
