mapper.sql:
FROM infrMember a LEFT JOIN infrMemberPhone b ON a.ifmmSeq = b.ifmmSeq LEFT JOIN infrMemberEmail c ON a.ifmmSeq = c.ifmmSeq WHERE 1=1
AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.ifmmName LIKE concat('%',#{shValue}, '%') AND a.ifmmId LIKE concat('%',#{shValue}, '%') AND b.ifmpNumber LIKE concat('%',#{shValue}, '%') AND c.ifmeEmailFull LIKE concat('%',#{shValue}, '%')

mapper.insert:
INSERT INTO infrMember (
ifmmName
,ifmmNameEng
,ifmmId
,ifmmDelNy
,ifmmPassword
,ifmmPassword2
,ifmmDob
,ifmmGenderCd
,regDateTime
,ifmmEmailConsentNy
,originalFileName
,uuidFileName
)
VALUES (
#{ifmmName}
,#{ifmmNameEng}
,#{ifmmId}
,0
,#{ifmmPassword}
,#{ifmmPassword2}
,#{ifmmDob}
,#{ifmmGenderCd}
,#{regDateTime}
,#{ifmmEmailConsentNy}
,#{originalFileName}
,#{uuidFileName}
)

	SELECT last_insert_id()

mapper.update:
UPDATE
infrMember  
SET
ifmmName = #{ifmmName} 
,ifmmNameEng = #{ifmmNameEng}
,ifmmGenderCd = #{ifmmGenderCd}
,ifmmEmailConsentNy = #{ifmmEmailConsentNy}
,ifmmId = #{ifmmId}
,ifmmPassword = #{ifmmPassword} 
,regDateTime = #{regDateTime}
,originalFileName = #{originalFileName}
,uuidFileName = #{uuidFileName}
WHERE
1 = 1
AND ifmmSeq = #{ifmmSeq}

UPDATE
infrMemberPhone
SET
ifmpNumber = #{ifmpNumber}
,ifmpTelecomCd = #{ifmpTelecomCd}
WHERE
1 = 1
AND ifmmSeq = #{ifmmSeq}

UPDATE
infrMemberAddress
SET
ifmaAddress1 = #{ifmaAddress1}
,ifmaAddress2 = #{ifmaAddress2}
,ifmaZipcode = #{ifmaZipcode}
,ifmaLat = #{ifmaLat}
,ifmaLng = #{ifmaLng}
,ifmmSeq = #{ifmmSeq}
WHERE
	1 = 1
AND ifmmSeq = #{ifmmSeq}

UPDATE
infrMemberNationality
SET
ifnSeq = #{ifnSeq}
,ifmmSeq = #{ifmmSeq}
WHERE
	1 = 1
AND ifmmSeq = #{ifmmSeq}
