mapper.sql:
FROM infrMember a LEFT JOIN infrMemberPhone b ON a.ifmmSeq = b.ifmmSeq LEFT JOIN infrmemberEmail c ON a.ifmmSeq = c.ifmmSeq WHERE 1=1
AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd} AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd} AND a.ifmmDob BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.ifmmName LIKE concat('%',#{shValue}, '%') AND a.ifmmId LIKE concat('%',#{shValue}, '%') AND b.ifmpNumber LIKE concat('%',#{shValue}, '%') AND c.ifmeEmailFull LIKE concat('%',#{shValue}, '%')

mapper.insert:
INSERT INTO infrMember (
ifmmName
,ifmmId
,ifmmDelNy
,ifmmPassword
,ifmmPassword2
,ifmmDob
,ifmmGenderCd
,regDateTime
,ifmmEmailConsentNy
,ifmmSmsConsentNy
,ifmmPushConsentNy
)
VALUES (
#{ifmmName}
,#{ifmmId}
,0
,#{ifmmPassword}
,#{ifmmPassword2}
,#{ifmmDob}
,#{ifmmGenderCd}
,#{regDateTime}
,#{ifmmEmailConsentNy}
,#{ifmmSmsConsentNy}
,#{ifmmPushConsentNy}
)

SELECT last_insert_id()

mapper.update:
UPDATE
	infrMember  
SET
	ifmmName = #{ifmmName} 
	,ifmmId = #{ifmmId}
	,ifmmPassword = #{ifmmPassword} 
	,regDateTime = #{regDateTime}
WHERE 
	1 = 1
AND ifmmSeq = #{ifmmSeq}
